import React, { useState } from 'react';
import Lightbox from 'react-image-lightbox';
import 'react-image-lightbox/style.css';

export const Certification = () => {
  const agileStrategicPic = '../../assets/images/certificates/Attestation_CIRFOSS.png';
  const agileFoundationPic = '../../assets/images/certificates/agile_foundation.png';

  const [isLightboxOpen, setIsLightboxOpen] = useState(false);
  const [imageToShow, setImageToShow] = useState('');

  const openLightbox = (image) => {
    setImageToShow(image);
    setIsLightboxOpen(true);
  };

  return (
    <div className={styles.sectionCertification}>
      {/* ... */}
      <div className='row'>
        <div className="col-md-6">
          <a className={styles.certificationBlock} href={agileStrategicPic} download>
            <div className="text-center">
              <p>
                <img
                  src={agileStrategicPic}
                  alt="Agile strategic pic"
                  width={150}
                  className='d-block mr-auto mx-auto'
                  onClick={() => openLightbox(agileStrategicPic)}
                  style={{ cursor: 'pointer' }}
                />
                <span className='d-block text-capitalize text-center my-2'>L'Agilité stratégique</span>
              </p>
            </div>
          </a>
        </div>
        {/* ... */}
      </div>

      {/* Lightbox */}
      {isLightboxOpen && (
        <Lightbox
          mainSrc={imageToShow}
          onCloseRequest={() => setIsLightboxOpen(false)}
        />
      )}
    </div>
  )
}
